import React, { Component } from 'react';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Button from 'react-bootstrap/Button';
import {NavDropdown} from 'react-bootstrap';
// import {Notes} from './Notes';


// import './App.css';

class Navigation extends Component {
  render() {
    return (
      <Navbar bg="light" expand="lg">
        <Navbar.Brand href="#home">LRo</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <Nav.Link href="#home">Products</Nav.Link>
            <Nav.Link href="#link">Notes
              <Notes />
            </Nav.Link>
            <Nav.Link href="#link">Sale</Nav.Link>
            <Nav.Link href="#link">Contact</Nav.Link>
          </Nav>


          <Nav className="mr-sm-2 mr-auto">
            <NavDropdown title="Sign In" id="basic-nav-dropdown">
              <NavDropdown.Item href="#action/3.4">Sign In</NavDropdown.Item>
              <NavDropdown.Divider />
              <NavDropdown.Item href="#action/3.1">Your Account</NavDropdown.Item>
              <NavDropdown.Item href="#action/3.2">Wish List</NavDropdown.Item>
              

            </NavDropdown>

            <Nav.Item>
              <Button href="#">
                <i class="fas fa-shopping-bag fa-lg"></i>
              </Button>
            </Nav.Item>

          </Nav>
        </Navbar.Collapse>
      </Navbar>
    );
  }
}

export default Navigation;

import Overlay from 'react-bootstrap/Overlay';


class Notes extends React.Component {
  constructor(...args) {
    super(...args);

    this.attachRef = target => this.setState({ target });
    this.state = {
      show: false,
    };
  }

  render() {
    const { show, target } = this.state;
    return (
      <>
        <Button
          variant="danger"
          ref={this.attachRef}
          onClick={() => this.setState({ show: !show })}
        >
          Notes
        </Button>
        <Overlay target={target} show={show} placement="right">
          {({ placement, scheduleUpdate, arrowProps, ...props }) => (
            <div
              {...props}
              style={{
                backgroundColor: 'rgba(255, 100, 100, 0.85)',
                padding: '2px 10px',
                color: 'white',
                borderRadius: 3,
                ...props.style,
              }}
            >
              <ul>
                <li>cedarwood</li>
                <li>sandalwood</li>
                <li>vanilla</li>
                <li> amber</li>
                <li>patchouli</li>
                <li>oakmoss</li>
                <li>musk</li>
              </ul>
            </div>
          )}
        </Overlay>
      </>
    );
  }
}

render(<Notes />);
